create database Newspaper;
use Newspaper;

create table Posts(
	id int primary key AUTO_INCREMENT,
    authorId int,
    title varchar(255),
    sub_category int,
    category int,
    publishDate date,
    postExcerpt text,
    content text,
    views int,
    imgLink varchar(255),
    approval int, 
    premium bool
);
/*
approval - xét duyệt
1 - Đã xuất bản
2 - Đã được duyệt & chờ xuất bản
3 - Bị từ chối
4 - Chưa được duyệt
*/

create table Categories(
	id int primary key,
    categoryName varchar(255)
);


create table SubCategories(
	id int,
    categoryId int,
    subcategoryName varchar(255),
    primary key (id, categoryId)
);

create table Tags(
	postId int,
    tagName varchar(255),
    primary key (postId, tagName)
);

create table Users(
	id int primary key AUTO_INCREMENT,
    username char(255) unique,
    passwordString char(200),
    userClass int,
    fullname char(100),    
    dabirthday date,
    email char(50)
);

/*
 user class
 1 - đọc giả - subscriber
 2 - phóng viên - writer
 3 - biên tập viên - editor
 4 - quản trị viên - administrator
*/
 Create table writer(
	id int primary key,
    pseudonym char(50)
 );
 create table subscriber(
	id int primary key,
    expirydate date
 );
 create table editor(
	id int primary key,
    TSEditor bool,
    KdEditor bool,
    GTEditor bool,
    TTEditor bool,
    DLEditor bool
 );
create table Comments(
	commentId int primary key,
    postId int,
    userId int,
    commentContent text
);
alter table SubCategories add constraint FK_SubCategories_Categories foreign key (categoryId) references Categories(id);
alter table Posts add constraint FK_Posts_SubCategories foreign key (sub_category, category) references SubCategories(id, categoryId);
alter table Posts add constraint FK_Posts_Tags foreign key (id) references Tags(postId);
alter table Posts add constraint FK_Posts_Users foreign key (authorId) references Users(id);
alter table Comments add constraint FK_Comments_Posts foreign key (postId) references Posts(id);










